import React from 'react'

export interface ColophonProps {
  department?: any
}

const Colophon = ({ department }: ColophonProps): JSX.Element => {
  const MizzouCopyrightYear = new Date().getFullYear().toString()
  let deparmentContact

  if (department) {
    deparmentContact = department.map((singleDepartment, i) => (
      <div key={i} className="miz-publisher">
        <p>
          <small>
            Published by {singleDepartment.name}, {singleDepartment.address}
          </small>
        </p>
        <p>
          <small>
            <strong>Email:</strong>{' '}
            <a href={`mailto:${singleDepartment.email}`}>{singleDepartment.email}</a>
          </small>
        </p>
      </div>
    ))
  }

  return (
    <div className="miz-colophon">
      <div className="miz-copyright">
        <p>
          <small>
            &copy; <time dateTime={MizzouCopyrightYear}>{MizzouCopyrightYear}</time> &#8212;
            Curators of the <a href="//www.umsystem.edu/">University of Missouri</a>. All rights
            reserved.{' '}
            <a href="https://missouri.edu/copyright/">DMCA and other copyright information</a>.{' '}
            <a href="https://missouri.edu/privacy/">Privacy policy</a>
          </small>
        </p>
      </div>

      {department && deparmentContact}
    </div>
  )
}

export default Colophon
