import React from 'react'

export interface EventProps {
  startDate: string
  endDate: string
  title: string
  link: string
  location?: string
  excerpt?: string
}

const Event = ({ startDate, title, link }: EventProps): JSX.Element => {
  // const dateStart = new Date(startDate);
  const dateStart = new Date(startDate)

  const month = new Intl.DateTimeFormat('en-US', { month: 'short' }).format(dateStart)
  const day = dateStart.getDate()

  return (
    <div className="miz-card miz-card--event">
      <div className="miz-event__details">
        <p className="miz-event__date">
          {month} {day}
        </p>
        <a href={link} className="stretched-link miz-event__title">
          {title}
        </a>
      </div>
    </div>
  )
}

export default Event
