import React from 'react'
import { AnchorButton } from 'Components/Button/Button'
import classNames from 'classnames'

export interface BigMessageProps {
  title: string | React.ReactNode
  children: React.ReactNode
  buttons?: any
  variant?: 'default' | 'dark'
}

const BigMessage = ({
  title = 'Big Message Title',
  children,
  buttons,
  variant = 'default',
}: BigMessageProps): JSX.Element => {
  let buttonGroup

  if (buttons) {
    buttonGroup = buttons.map((button, i) => (
      <AnchorButton
        branded={true}
        primary={variant === 'dark'}
        secondary={variant === 'default'}
        link={button.link}
        key={i}
      >
        {button.title}
      </AnchorButton>
    ))
  }

  const sectionClass = classNames(
    'miz-layer',
    'miz-layer--brand',
    'miz-fill--black-100',
    'miz-decoration miz-decoration__plus',
    'miz-decoration__plus--bottom-right',
    'miz-main-grid__full',
  )

  const frameClass = classNames('miz-big-message__frame', {
    'miz-fill--white': variant !== 'dark',
    'miz-big-message__frame--dark': variant === 'dark',
  })

  return (
    <section className={sectionClass}>
      <div className="miz-container">
        <div className="miz-decoration miz-decoration__corner--top-left">
          <div className={frameClass}>
            <header className="miz-layer__header miz-layer__header--center">
              <h2 className="miz-layer__title">{title}</h2>
            </header>
            <div className="miz-layer__content miz-layer__content--center miz-big-message__content">
              {children}

              {buttons && <>{buttonGroup}</>}
            </div>
          </div>
        </div>
      </div>
    </section>
  )
}

export default BigMessage
