import React from 'react'
import classNames from 'classnames'
import DrupalAttribute from 'drupal-attribute'
import LinkList, { LinkListProps } from 'Components/LinkList/LinkList'

export interface LinkListLayerProps extends LinkListProps {
  title?: string | React.ReactNode
}

const LinkListLayer = ({
  attributes = new DrupalAttribute(''),
  title = 'Link List Title',
  list,
}: LinkListLayerProps): JSX.Element => {
  const LinkListLayerAttributes = {
    className: classNames('miz-layer', 'miz-main-grid__full', attributes.get('class')),
  }

  attributes.forEach((value, key) => {
    if (key !== 'class') {
      LinkListLayerAttributes[key] = value
    }
  })

  return (
    <section {...LinkListLayerAttributes}>
      <div className="miz-container">
        {title && (
          <header className="miz-text--center">
            <h2 className="miz-graphik">{title}</h2>
          </header>
        )}

        {list && (
          <LinkList
            attributes={attributes.addClass('miz-link-list--layer')}
            layout={'inline'}
            branded={true}
            list={list}
          />
        )}
      </div>
    </section>
  )
}

export default LinkListLayer
