/**
 * @format
 */

export type GridColumns = 2 | 3 | 4

export type GridSetting = {
  name?: string
  columns?: GridColumns
  gap?: boolean
  className?: string
  isResponsive?: boolean
}

export type GridSettings = GridSetting | GridSetting[]

export type GridLayout = {
  name: string
  type: 'grid'
  settings: GridSettings
}

export const GridLayoutDefault: GridLayout = {
  name: 'grid_default',
  type: 'grid',
  settings: {
    isResponsive: false,
  },
}
