import React from 'react'
import DrupalAttribute from 'drupal-attribute'
import classNames from 'classnames'

// import Nav from '../nav/Nav'
// import './_megamenu.scss'

export interface MegaMenuProps {
  attributes: DrupalAttribute
}

const MegaMenu = ({ attributes = new DrupalAttribute('') }: MegaMenuProps): JSX.Element => {
  const [megaMenuClass, setMegaMenuClass] = React.useState('miz-megamenu')

  const atts: Record<string, unknown> = { className: megaMenuClass }

  React.useEffect(() => {
    setMegaMenuClass(classNames('miz-megamenu'))

    attributes.forEach((value, att) => {
      if (att === 'class') {
        atts.className = value.join(' ')
      } else {
        atts[att] = value
      }
    })
  })

  return <div {...atts}></div>
}

export default MegaMenu
