import React from 'react'
import classNames from 'classnames'
import DrupalAttribute from 'drupal-attribute'

export interface PaginationProps {
  attributes?: DrupalAttribute
  children: React.ReactNode
}

const Pagination = ({
  attributes = new DrupalAttribute(''),
  children,
  ...props
}: PaginationProps): JSX.Element => {
  const PaginationAttributes = {
    className: classNames('miz-pagination', attributes.get('class')),
  }

  attributes.forEach((value, key) => {
    if (key !== 'class') {
      PaginationAttributes[key] = value
    }
  })

  return (
    <nav {...PaginationAttributes} {...props}>
      <ul className="miz-pagination__list">
        <li className="miz-pagination__list-item">
          <a href="#" className="miz-pagination__link">
            Prev
          </a>
        </li>
        <li className="miz-pagination__list-item">
          <a href="#" className="miz-pagination__link">
            1
          </a>
        </li>
        <li className="miz-pagination__list-item is-current">2</li>
        <li className="miz-pagination__list-item">
          <a href="#" className="miz-pagination__link">
            3
          </a>
        </li>
        <li className="miz-pagination__list-item">
          <a href="#" className="miz-pagination__link">
            Next
          </a>
        </li>
      </ul>
      {children}
    </nav>
  )
}

export default Pagination
