import * as React from 'react'
import DrupalAttribute from 'drupal-attribute'
import classNames from 'classnames'

export interface SocialMediaProps {
  attributes?: DrupalAttribute
  size?: 'sm' | 'md' | 'lg' | 'xl'
  color?: 'default' | 'black' | 'white' | 'gold'
  name: 'facebook' | 'twitter' | 'linkedin' | 'youtube' | 'instagram' | 'google' | 'snapchat'
}

const SocialMedia = ({
  attributes = new DrupalAttribute(''),
  size = 'md',
  color = 'default',
  name,
}: SocialMediaProps): JSX.Element => {
  const SocialMediaAttributes = {
    className: classNames(
      'miz-icon miz-icon__svg',
      {
        [`miz-icon--${size}`]: size,
        [`miz-svg--${color}`]: color,
        [`miz-svg--${name}`]: name,
      },
      attributes.get('class'),
    ),
    'aria-hidden': true,
  }

  return (
    <svg {...SocialMediaAttributes}>
      <use xlinkHref={`/images/icons/svg/sprite.symbol.svg#${name}`}></use>
    </svg>
  )
}

export default SocialMedia
